import React, { useState } from "react";
import TodoFormE from "./TodoFormE";
import { CgCloseO } from "react-icons/cg";
import { FiEdit } from "react-icons/fi";

function Todo({ tasks, completeTask, removeTask, updateTask, value}) {
  const [update, setUpdate] = useState({
    id: null,
    value: "",
  });

  const SubmitUpdate = value => {
    updateTask(update.id, value)
    setUpdate({
        id: null,
        value: ''
    })
  }


  const updatevalue = value =>{
    updateTask(update.id, value)
    setU({
      id: null,
      value: ''
  })
  }
  function filter(completed, show){
    //determines if the task should be show or not
    if(show=="all"){
      return true
    }else if(show=="completed"){
      if(completed)
        return true
    }else if(show=="incompleted"){
      if(!completed)
        return true
    }else{
      return false
    }

  }
  
  if(update.id) {
    return <TodoFormE edit={update} onSubmit={SubmitUpdate} />;
  }
  return tasks.map((task, index) => (
    //checks if the task should be shown and shows if is true
    filter(task.isComplete,value) == true &&
    <div
      className={task.isComplete ? "task-row complete" : "task-row"}
      key={index}
    >
      
      <div key={task.id} onClick={() => completeTask(task.id)}>
        {task.text}
      </div>
      <div className="icon">
        <CgCloseO onClick={() => removeTask(task.id)} className="delete-icon" />
        <FiEdit
          onClick={() => setUpdate({ id: task.id, value: task.text })} 
          className="edit-icon"
        />c
      </div>
    </div>
  
  ));
}

  
export default Todo;
